import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

class UserList extends Component {
    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
        this.getUserList();
        this.getActiveUserList();
    }

    // Firebaseからのデータ取得
    getUserList() {
        let db = firebase.database();
        let ref = db.ref('friend_info/1');
        let self = this;
        ref.orderByKey().on('value', (snapshot) => {
            console.log(snapshot.val());
            self.setState({
                data: snapshot.val()
            });
        });
    }

    // アクティブユーザのみデータ取得
    getActiveUserList() {
        let activeUserList = [];
        let db = firebase.database();
        let ref;
        let self = this;
        console.log("通ってます。");

        for (let i in this.state.data) {
            if (this.state.data[i].active_flag == 0) {
                // break;
            }
            ref = db.ref('user_info/' + i);
            console.log('i:' + i);
            ref.orderByKey().on('value', (snapshot) => {
                activeUserList.push(
                    {
                        i : {
                            'user_name': snapshot.val().user_name,
                            'address': snapshot.val().address,
                            'my_id': snapshot.val().my_id
                        }
                    }
                );
                console.log('snapshot' + snapshot.val());
                console.log('ac:' + activeUserList[0].keys(2).user_name);
                
                    
    
                });
        }
        // self.setState({
        //     data: activeUserList
        // });
        console.log('activeLength:' + activeUserList)
        console.log('dataLength:' + this.state.data.length)
    }

    // データ表示の生成
    getTableData() {
        let result = [];
        console.log(this.state.data);
        if (this.state.data == null || this.state.data.length == 0) {
            return [<tr key="0"><th>NO DATA.</th></tr>];
        }
        for (let i in this.state.data) {
            result.push(
                <tr>
                    <td class="user_image">image</td>
                    <td class="user_name">{this.state.data[i].active_flag}</td>
                </tr>
            );
        }
        return result;
    }

    render() {
        if (this.state.data.length == 0) {
            this.getUserList();
            
        }
        this.getActiveUserList();
        return (
            <table>
                <tbody>
                    {this.getTableData()}
                </tbody>
            </table>
        )
    }
}
export default UserList;
