import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';
import Lib from '../static/chat_lib';

class AddFriend extends Component {

    constructor(props) {
        super(props);
        this.state = {
            address: '',
            friendName: null
        }
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.doSearch = this.doSearch.bind(this);
    }

    // 入力されたアドレスをステートにセット
    onChangeAddress(e) {
        this.setState({
            address: Lib.encodeAddress(e.target.value)
        })
    }

    // アドレスからユーザIDを検索する
    doSearch() {
        let key = this.state.address;
        let db = firebase.database();
        let ref = db.ref('address_info/' + key);
        let userId;
        ref.orderByKey().on('value', (snapshot) => {
            userId = snapshot.val();
            this.setState({
                friendName: ''
            })
            console.log('userId:' + userId);
            if (userId != null) {
                this.getUserInfo(userId);
            }
        });
    }

    // ユーザIDからユーザ情報を取得する
    getUserInfo(userId) {
        let db = firebase.database();
        let ref = db.ref('user_info/' + userId);
        ref.orderByKey().on('value', (snapshot) => {
            let res = snapshot.val();
            console.log('userInfo:' + JSON.stringify(snapshot.val()));
            this.setState({
                friendName: res.user_name
            })
        });
    }

    // 検索結果を表示する
    createFriendInfo() {
        let result;
        if (this.state.friendName == '') {
            result = ['※入力されたアドレスに紐づくユーザは存在しません。'];
        } else {
            result = [<div><span>{this.state.friendName}</span><button>追加</button></div>]
        }
        console.log('result:' + result);
        return result;
    }

    render() {
        return (
            <div>
                メールアドレス：<input type="text" size="40" value={this.state.address} onChange={this.onChangeAddress} />
                <button onClick={this.doSearch}>検索</button>
                {this.state.friendName != null
                ?
                <div>
                    {this.createFriendInfo()}
                </div>
                :
                <div>
                    ※追加したい友人のメールアドレスを入力して「検索」をクリックしてください。
                </div>
                }
            </div>
        );
    }
}
export default AddFriend;
