import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

class UserList extends Component {
    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
        this.getUserList();
    }

    // Firebaseからのデータ取得
    getUserList() {
        let db = firebase.database();
        let ref = db.ref('friend_info/1');
        let ref2;
        let dataList = [];
        let self = this;
        ref.orderByKey().on('value', (snapshot) => {
            for (let i in snapshot.val()) {
                console.log(i);
                if (snapshot.val()[i].active_flag == 0) {
                    break;
                }
                ref2 = db.ref('user_info/' + i);
                ref.orderByKey().on('value', (snapshot2) => {
                    console.log('data:' + snapshot2.val());
                    dataList.push(snapshot2.val());
                })
            }
            self.setState({
                data: dataList
            });
            console.log('dataList:' + dataList);
            console.log('dataです:' + data);
        });
    }

    // データ表示の生成
    getTableData() {
        let result = [];
        if (this.state.data == null || this.state.data.length == 0) {
            return [<tr key="0"><th>NO DATA.</th></tr>];
        }
        for (let i in this.state.data) {
            result.push(
                <tr>
                    <td class="user_image">image</td>
                    <td class="user_name">{this.state.data[i].user_name}</td>
                </tr>
            );
        }
        return result;
    }

    render() {
        if (this.state.data.length == 0) {
            this.getUserList();
        }
        return (
            <table>
                <tbody>
                    {this.getTableData()}
                </tbody>
            </table>
        )
    }
}
export default UserList;
