import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

class UserList extends Component {
    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
        this.getUserList();
    }

    // Firebaseからのデータ取得
    getUserList() {
        let db = firebase.database();
        let ref = db.ref('friend_info/1');
        let self = this;
        ref.orderByKey().on('value', (snapshot) => {
            self.setState({
                data: snapshot.val()
            });
        });

    }

    // データ表示の生成
    getTableData() {
        let result = [];
        if (this.state.data == null || this.state.data.length == 0) {
            return [<tr key="0"><th>NO DATA.</th></tr>];
        }
        for (let i in this.state.data) {
            console.log(this.state.data[i]);
            console.log(i);
            if (this.state.data[i].active_flag != 1) {
                break;
            }

            
            result.push(
                <tr key={i}>
                    <th>{this.state.data[i].active_flag}</th>
                    <td>{this.state.data[i].create_date}</td>
                    <td>{this.state.data[i].update_date}</td>
                </tr>
            );
    
        }
        return result;
    }

    render() {
        if (this.state.data.length == 0) {
            this.getUserList();
        }
        return (
            <table>
                <tbody>
                    {this.getTableData()}
                </tbody>
            </table>
        )
    }
}
export default UserList;
