import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

class UserList extends Component {
    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
        this.getUserList();
    }

    // Firebaseからのデータ取得
    getUserList() {
        let db = firebase.database();
        let ref = db.ref('friend_info/1');
        let self = this;
        ref.orderByKey().on('value', (snapshot) => {
            self.setState({
                data: snapshot.val()
            });
        });
    }

    // データ表示の生成
    getTableData() {
        let result = [];
        let db = firebase.database();
        let ref;
        if (this.state.data == null || this.state.data.length == 0) {
            return [<tr key="0"><th>NO DATA.</th></tr>];
        }
        for (let i in this.state.data) {
            if (this.state.data[i].active_flag == 0) {
                break;
            }
            ref = db.ref('user_info/' + i);
            ref.orderByKey().on('value', (snapshot) => {
                console.log("１番目"+snapshot.val().user_name);
                result.push(
                    <tr>
                        <td class="user_image">image</td>
                        <td class="user_name">{snapshot.val().user_name}</td>
                    </tr>
                );
            });
        }
        console.log("2番目");
        return result;
    }

    render() {
        if (this.state.data.length == 0) {
            this.getUserList();
        }
        return (
            <table>
                <tbody>
                    {this.getTableData()}
                </tbody>
            </table>
        )
    }
}
export default UserList;
