import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';
import Lib from '../static/chat_lib';
import Router from 'next/router';

class TalkRoom extends Component {

    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            friendId: Router.query.id,
            talkData: []
        }
        console.log(Router.query.id);
    }

    // トーク内容を取得
    getTalkData(friendId) {
        let db = firebase.fatabase();
        let ref = db.ref('messages/1/' + friendId);
        let self = this;
        let d = [];
        ref.orderByKey().on('value', (snapshot) => {
            d = Lib.deepcopy(snapshot.val());
            self.setState({
                talkData: d
            });
        })
    }

    go() {
        Router.push('/home');
    }

    render() {
        return (
            <div>Hello.</div>

        )

    }



}
export default TalkRoom;
