import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

class AddFriend extends Component {

    constructor(props) {
        super(props);
        this.state = {
            address: ''
        }
    }

    // 入力されたアドレスをステートにセット
    onChangeAddress(e) {
        this.setState({
            address: e.target.value
        })
    }

    // ユーザを検索する
    doSearch() {
        let key = this.state.address;
        let db = firebase.database();
        let ref = db.ref('user_info/' + key);
        ref.orderByKey().on('value', (snapshot) => {
            console.log('friend:' + JSON.stringify(snapshot.val()));
        });
    }

    render() {
        return (
            <div>
                メールアドレス：<input type="text" size="40" value={this.state.address} onChange={this.onChangeAddress} />
                <button>検索</button>
                <div>
                    ※追加したい友人のメールアドレスを入力して「検索」をクリックしてください。
                </div>
            </div>
        );
    }
}
export default AddFriend;
