import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';
import Lib from '../static/chat_lib';
import Router from 'next/router';

class TalkRoom extends Component {

    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            friendId: Router.query.id,
            talkData: []
        }
        console.log(Router.query.id);
        this.getTalkData(this.state.friendId);
    }

    // トーク内容を取得
    getTalkData(friendId) {
        let db = firebase.fatabase();
        let ref = db.ref('messages/1/' + friendId);
        let self = this;
        let d = [];
        ref.orderByKey().on('value', (snapshot) => {
            d = Lib.deepcopy(snapshot.val());
            console.log(snapshot.val());
            self.setState({
                talkData: d
            });
        });
    }

    // トーク履歴を作成
    createTalkData() {
        let result = [];
        if (this.state.talkData == null || this.state.talkData.length == 0) {
            return [<tr><td>No Talk Data.</td></tr>];
        }
        for (let i in this.state.talkData) {
            result.push(
                <tr>
                    <td>
                        {this.state.talkData[i].content}
                    </td>
                    <td>
                        {this.state.talkData[i].create_date}
                    </td>
                </tr>
            );
        }
        return result;
    }

    go() {
        Router.push('/home');
    }

    render() {
        return (
            <div>
                <table>
                    <tbody>
                        {this.createTalkData()}
                    </tbody>
                </table>
                <button onClick={this.go.bind()}>back</button>
            </div>
        );
    }
}
export default TalkRoom;
