import React, { Component } from 'react';
import firebase from 'firebase';
import 'firebase/storage';
import Router from 'next/router';

class TalkList extends Component {
    style = {
        fontSize: '12pt',
        padding: '5px 10px'
    }

    constructor(props) {
        super(props);
        this.state = {
            data: [],
            activeUserData: [],
            talkData: []
        }
        this.getUserList();
    }

    // 友人データ取得
    getUserList() {
        let db = firebase.database();
        let ref = db.ref('friends/1');
        let self = this;
        ref.orderByKey().on('value', (snapshot) => {
            self.setState({
                data: snapshot.val()
            }, () => {this.getActiveUserList();});
        });
    }

    // アクティブユーザのみデータ取得
    getActiveUserList() {
        let activeUserList = [];
        let db = firebase.database();
        let ref;
        let self = this;

        for (let i in this.state.data) {
            if (this.state.data[i].active_flag == 0) {
                break;
            }
            ref = db.ref('user_info/' + i);
            ref.orderByKey().on('value', (snapshot) => {
                activeUserList.push(snapshot.val());
                self.setState({
                    activeUserData: activeUserList
                });
            });
        }
    }

    // トークリスト取得
    getTalkList() {
        let talkList = [];
        let db = firebase.database();
        let ref;
        let self = this;

        for (let i in this.state.activeUserData) {
            ref = db.ref('messages/1/' + i);
            ref.orderByKey().limitToLast(1).on('value', (snapshot) => {
                talkList.push(snapshot.val());
                self.setState({
                    talkData: talkList
                });
            });
        }
    }

    // 表示するトークリストの生成
    createTalkList() {
        let result = [];
        if (this.state.talkData == null || this.state.talkData.length == 0) {
            return [<tr key="0"><th>NO DATA.</th></tr>];
        }
        for (let i in this.state.talkData) {
            result.push(
                <tr data-tag={this.state.talkData[i].id}>
                    <td>username</td>
                    <td>
                        {this.state.talkData[i].content}
                    </td>
                </tr>
            );
        }
        return result;
    }

    render() {
        return (
            <div>
                {/* {0 == 0
                ?
                <p>Please wait...</p>
                : */}
                <table>
                    <tbody>
                        {this.createTalkList()}
                    </tbody>
                </table>
                {/* } */}
            </div>
        );
    }
}

export default TalkList;
